package org.example;
public class BowlingGame {
  private int roll1;
  private int roll2;

  public BowlingGame(int roll1, int roll2) {
    this.roll1 = roll1;
    this.roll2 = roll2;
  }

  public int score() {
    return roll1 + roll2;
  }

}
